definition.script:
pipeline {
    agent { node { label 'master' } }
     parameters {
        string(name: 'SuggestedStreamName', defaultValue: '', description: 'What should the new stream be named with? \n Prefered syntax:Arhcer_17.12_TASKNAme_WINum')
        string(name: 'SourceStreamName', defaultValue: '', description: 'What is the base stream name?')
        string(name: 'SourceStreamSnapshotName', defaultValue: '', description: 'What is the snapshot name you would use as a base for the new stream?')
    }
    environment {

        search_snaposhots_cmd="scm list snapshots -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -n ${params.SourceStreamSnapshotName}"
        search_source_stream_cmd="scm list stream -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -n ${params.SourceStreamName}"
        search_target_stream_cmd="scm list stream -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -n ${params.SuggestedStreamName}"
    }
        stages {
    			stage ('verify') {
    				steps {
        				    script {
                                wrap([$class: 'BuildUser']) {
            				        currentBuild.description = "Request for new stream creation '${params.SuggestedStreamName}' by '${env.BUILD_USER}'"
                                }

    	    					print 'verify suggested stream name is not occupied'
    						    switch(searchStrInCommandOutput(search_target_stream_cmd,params.SuggestedStreamName) >= 1){
    						        case 1..Integer.MAX_VALUE :
    							       throw new Exception("Stream name " + params.SuggestedStreamName + " is already occupied")
                                    default:
                                        print 'suggested stream name is free'
    						    }

    	    					print 'verify source stream name'
    	    					switch(searchStrInCommandOutput(search_source_stream_cmd,params.SourceStreamName)){
    	    					    case 2..Integer.MAX_VALUE :
    	    						        throw new Exception('Multi stream with prefix ' + params.SourceStreamName)
    	    					    case Integer.MIN_VALUE+2..0 :
    	    						        throw new Exception( 'Invalid source stream name '  + params.SourceStreamName)
    	                            default:
    	                                print 'source stream name is verified'
    	    					}

    	    					print 'verify suggested stream snapshot'
    	        				switch(searchStrInCommandOutput(search_snaposhots_cmd,params.SourceStreamSnapshotName)){
    	        				    case 2..Integer.MAX_VALUE :
    	                                    throw new Exception("Multi snapshots with prefix "+params.SourceStreamSnapshotName)
    	                            case Integer.MIN_VALUE+2..0 :
    	        						    throw new Exception('Invalid snapshot name: '+params.SourceStreamSnapshotName+' for stream: ' +params.SourceStreamName)
                                    default:    
    					                    print 'snapshot name is verified'
                                }   
        				    }
    				}
    			}
    			stage ('approve') {
    			 steps {
    			     script {
    			         //def userInput
    			         try{
    			             mail (to: 'adham.saffori@kla-tencor.com',
                                 subject: "New stream request with name  '${params.SuggestedStreamName}' is waiting for your approval",
                                 body: "Please go to ${env.BUILD_URL}pipeline.");
            			     env.userInput = input(
            				 id: 'userInput', message: 'Please approve the below stream name?', parameters: [
            				 [$class: 'StringParameterDefinition', defaultValue: params.SuggestedStreamName, description: 'new stream name', name: 'userInput']
            				],submitter: 'DL-OMD-ENG-SCM',ok:'Approved, lets create it')
            			//	print (userInput)
    			         } catch (err) { // input false
    			            print("fdsfsdd" + err)
                            env.userInput = input(
            				 id: 'userInput', message: 'Please update abortion reason?', parameters: [
            				[$class: 'TextParameterDefinition', defaultValue: '', description: 'Decline reason', name: 'declineReason']]
            				,submitter: 'DL-OMD-ENG-SCM',ok: 'Email user, close request')
                            def user = err.getCauses()[0].getUser()
                            echo "Aborted by: [${user}]"
                            echo "Abort reason: "+ env.userInput
                            //throw new Exception( "Abort reason: "+ userInput)
                            wrap([$class: 'BuildUser']) {
                                emailext (
                                    subject: "Declined: Stream creation request ${params.SuggestedStreamName}'",
                                    body: """<p>Declined by: '${user}'</p>
                                    <p>Reason: '${env.userInput}'</p>
                                    <p>For execution details, please check console output at "<a href="${env.BUILD_URL}">${env.JOB_NAME} [${env.BUILD_NUMBER}]</a>"</p>""",
                                     to: "${BUILD_USER_EMAIL}"
                                )
                            }
                        }
                        print (env.userInput)
    				if (!params.SuggestedStreamName.equals(env.userInput)){ 
    				        print 'verify new suggested stream name is not occupied '+ env.userInput
						    switch(searchStrInCommandOutput(search_target_stream_cmd,params.SuggestedStreamName) >= 1){
						        case 1..Integer.MAX_VALUE :
							       throw new Exception("Stream name " + env.userInput + " is already occupied")
                                default:
                                    print 'new suggested stream name is free'
						    }
    			     }
    			 }
    			}
    			}
    			stage ('create') {
                    steps {
                        script {
                            create_stream_cmd="scm create stream -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& --stream  ${params.SourceStreamName} --snapshot ${params.SourceStreamSnapshotName}  --projectarea \"OMD NuArcher\"	 ${env.userInput}"
                            switch(searchStrInCommandOutput(create_stream_cmd," successfully created.")){
						        case 0:
							       throw new Exception("Stream creation faild")
                                default:
                                    print 'New stream was successfully created'
						    }

    				        //echo "scm create stream -r http://clm.kla-tencor.com/ccm -u omdbuild -p Scm07^& -n ${userInput['userInput']} ${params.SourceStreamName}"
                        }
                    }
    			}

    }
}

def searchStrInCommandOutput(cmd,str){
    stdoutput = bat(returnStdout:true , script:cmd).trim()
    //print ("stdout=" + stdout)
    result = stdoutput.readLines().drop(1).join(" ")
    //print ("count=" +result.count(str))
    return result.count(str)
}
