definition.script:
pipeline {
    agent { node { label 'master' } }
     parameters {
        string(name: 'SourceStreamSnapshotName', defaultValue: '', description: 'What is the snapshot name you would use as a base for the new stream?')
        string(name: 'SourceStreamName', defaultValue: '', description: 'What is the base stream name?')
        string(name: 'SuggestedStreamName', defaultValue: '', description: 'What should the new stream be named with? \n Prefered syntax:Arhcer_bla_bla_bla')
    }
    environment {
        search_snaposhots_cmd="scm list snapshots -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -n ${params.SourceStreamSnapshotName}"
        search_source_stream_cmd="scm list stream -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -n ${params.SourceStreamName}"
        search_target_stream_cmd="scm list stream -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -n ${params.SuggestedStreamName}"
    }
        stages {
    			stage ('verify') {
    				steps {
    				    script {
    					 print 'verify suggested stream name is not occupied'
					    switch (searchStrInCommandOutput(search_target_stream_cmd,params.SuggestedStreamName) ) {
					        case 1..Integer.MAX_VALUE :
						        error 'Stream name '+params.SuggestedStreamName+' is already occupied'
						        break
						    default:
						        print 'suggested stream name is free'
                                break
					    }

    					print 'verify source stream name'
                        print searchStrInCommandOutput(search_source_stream_cmd,params.SourceStreamName)
    					     switch (searchStrInCommandOutput(search_source_stream_cmd,params.SourceStreamName) ) {
    					        case 2..100 :
    						        error 'Multi stream with prefix '+params.SourceStreamName
    						    case -100..0:
    						        error 'Invalid source stream name '+params.SourceStreamName
    						    case 1:
    						        print 'source stream name is verified'
    						    default:
    						        error 'Invalid source stream name ' + params.SourceStreamName
    					    }

    					print 'verify suggested stream snapshot'
    					switch (searchStrInCommandOutput(search_target_stream_cmd,params.SourceStreamSnapshotName) ) {
    					        case 2..100 :
    						        error 'Multi snapshots with prefix '+params.SourceStreamSnapshotName
    						        break
    						    case -100..0:
    						        error 'Invalid snapshot name: '+params.SourceStreamSnapshotName+' for stream: ' +params.SourceStreamName
    						        break
    						    case 1:
    						        print 'snapshot name is verified'
    						    default:
    						        error 'Invalid source stream snapshot name ' + params.SourceStreamSnapshotName
                                    break
    					    }
    				    }
    				}
    			}
    			stage ('approve') {
    			 steps {
    			     script {
    				 env.userInput = input(
    				 id: 'userInput', message: 'Let\'s create it?', parameters: [
    				 [$class: 'StringParameterDefinition', defaultValue: params.SuggestedStreamName, description: 'new stream name', name: 'userInput']
    				])
    				echo ("Creating Stream:"+ env.userInput)
    			     }
    			 }
    			}
    			stage ('create') {
                    steps {
                        script {
    				        echo "scm create stream -r http://clm.kla-tencor.com/ccm -u omdbuild -p Scm07^& -n ${env.userInput} ${params.SourceStreamName}"
                        }
                    }
    			}
    		}

}

def searchStrInCommandOutput(cmd,str){
    stdout = bat(returnStdout:true , script: cmd).trim()
    print ("stdout=" + stdout)
    result = stdout.readLines().drop(1).join(" ")
    print ("count=" +result.count(str))
    return result.count(str)
}
