definition.script:
pipeline {
    agent { node { label 'master' } }
        stages {
    			stage ('verify') {
    				steps {
    				    script {
    					 print 'verify suggested stream name is not occupied'
    					if (getCommandOutput('scm list stream -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -n ${params.SuggestedStreamName}') !=""){
    						print ("Stream name '"+params.SuggestedStreamName+"' is already occupied") 
    						error 'Stream name '+params.SuggestedStreamName+' is already occupied'
    					}
    					print 'verify source stream name'
    					if (getCommandOutput('scm list stream -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -n ${params.SourceStreamName}') == "") {
    						print ("Invalid source stream name '" + params.SourceStreamName) 
    						error 'Invalid source stream name ' + params.SourceStreamName
    					}
    					print 'verify suggested stream snapshot'
    					if (getCommandOutput('scm list stream -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -n ${params.SuggestedStreamName}') == ""){ 
    						print ("Invalid source stream snapshot name '" + params.SourceStreamSnapshotName)
    						error 'Invalid source stream snapshot name ' + params.SourceStreamSnapshotName
    					}
    				    }
    				}
    			}
    			stage ('approve') {
    			 steps {
    			     script {
    				 userInput = input(
    				 id: 'userInput', message: 'Let\'s create it?', parameters: [
    				 [$class: 'TextParameterDefinition', defaultValue: params.SuggestedStreamName, description: 'new stream name', name: 'stream'],
    				])
    				echo ("Creating Stream: "+userInput['stream'])
    			     }
    			 }
    			}
    			stage ('create') {
                    steps {
    				    bat 'scm create stream -r http://clm.kla-tencor.com/ccm -u omdbuild -p Scm07^& -n $SuggestedStreamName'
                    }
    			}
    		}

}

def getCommandOutput(cmd){
    stdout = bat(returnStdout:true , script: cmd).trim()
    result = stdout.readLines().drop(1).join(" ")       
    return result
}
