definition.script:
pipeline {
allocated_nodes=[]
agent { node { label 'master' } }
parameters {
        string(defaultValue: "[]", description: 'What tests needs to run and in which confgurations? \n example: [test1:pipe,test2:pipe,test3:pipe]', name: 'TestsArray')
    }
    stages {
        stage ("Run tests"){

            steps{
                script{
                print params.TestsArray
                def stringMap= params.TestsArray.replaceAll('\\[|\\]','')
                def newMap = [:]
                stringMap.tokenize(',').each {
                    kvTuple = it.tokenize(':')
                    newMap[kvTuple[0]] = kvTuple[1]
                }
                print "newMap="+newMap
                runparalleltests(newMap)
                }

            }
        }
    }

}
// in this array we'll place the jobs that we wish to run
def runparalleltests(AUT_Tests){
def tests = [:]
//print "params.TestsArray"+params.TestsArray
//running the job 4 times concurrently
//the dummy parameter is for preventing mutation of the parameter before the execution of the closure.
//we have to assign it outside the closure or it will run the job multiple times with the same parameter "4"
//and jenkins will unite them intgetInstanceo a single run of the job
  //AUT_Tests=(params.TestsArray).toMapString()
//for (int i = 0; i < AUT_Tests.size(); i++) {
def i=0
AUT_Tests.each{    
 //def index = i //if we tried to use i below, it would equal 4 in each job execution.
 print it.value
    print it.key
    print ("node="+getNodePerLabel("pipe"))
  tests["test${i}"] = {
//Parameters:
//param1 : an example string parameter for the triggered job.
//dummy: a parameter used to prevent triggering the job with the same parameters value.
//this parameter has to accept a different value each time the job is triggered.
//def value=it.value
//def jobname=it.key
    build job:it.key,parameters: [
      string(name: 'name', value:'test_param'),
      [$class: 'NodeParameterValue', name: 'node', labels: [it.value], nodeEligibility: [$class: 'AllNodeEligibility']]]
  }
  i++
}
parallel tests
}


@NonCPS
def getNodePerLabel(String label){
    def labelNodes=Jenkins.getInstance().getLabel(label)
    def nodes=labelNodes.getNodes()
    def nodeName=null
    for (node in nodes) {
        if (!node.getComputer().isOffline()){ 
                 print node.getComputer().getNumExecutors()
                 print node.getComputer().countBusy()
                if ((node.getComputer().getNumExecutors()-node.getComputer().countBusy() >0) && !(node.getNodeName() in allocated_nodes)){
                    nodeName=node.getNodeName()
                    allocated_nodes << nodeName
                    break;
                }
             }
    }
        if (nodeName == null)
            nodeName=nodes[0].getNodeName();
    return nodeName
}
