definition.script:
//This list is used to save nodes which were already alocated by one of the tests so that tests will be spreaded to diffrent nodes 
allocated_nodes=[]

pipeline {
agent { node { label 'master' } }
parameters {
        string(defaultValue: "", description: 'What is the Archer build version you want to test? \n example: 17.12.00.20600', name: 'BUILD_VERSION')
        choice(choices: 'Tasks\nFormal\nScheduled', description: 'What is your build type?', name: 'BUILDTYPE')
        text(description: 'What tests needs to run and in which confgurations? \n example: [[SuiteName,SuitePath,ToolType,ATEControllerMachineLabel,SimulatorEngineMachineLabel,BaseImagePath,BaseImageName,ATEScriptsPath]]', name: 'TestsArray')
    }

    stages {
        stage ("Run Tests"){

            steps{
                script{
                    //Delete new line characters and read all test arrays 
                    def stringMap= params.TestsArray.replaceAll("[\n\r]", "").replaceAll('^\\[|\\]$','')
                    //send all test arrays to processing
                    runparalleltests(stringMap)
                }

            }
        }
    }

}
// Run multiple ATE tests
def runparalleltests(AUT_Tests){
def tests = [:]
//print "params.TestsArray"+params.TestsArray
//running the job 4 times concurrently
//the dummy parameter is for preventing mutation of the parameter before the execution of the closure.
//we have to assign it outside the closure or it will run the job multiple times with the same parameter "4"
//and jenkins will unite them intgetInstanceo a single run of the job
  //AUT_Tests=(params.TestsArray).toMapString()
//for (int i = 0; i < AUT_Tests.size(); i++) {
def i=0
//                }
AUT_Tests.tokenize(';').each{    
 //def index = i //if we tried to use i below, it would equal 4 in each job execution.

                    testDetails = it.replaceAll('^\\[|\\]$','').tokenize(',')
                    def test_suite_name = testDetails[0]
					def test_suite_path = testDetails[1]
					def tool_type = testDetails[2]
					def ate_controller_engine_label = testDetails[3]
					def simulator_engine_label = testDetails[4]
					def base_image_path = testDetails[5]
					def base_image = testDetails[6]
					def ate_scripts_path = testDetails[7]
 //print it.value
    //print it.key
    //print ("node="+getNodePerLabel("pipe"))
  tests["Test ${test_suite_name} for ${tool_type}"] = {
//Parameters:
//param1 : an example string parameter for the triggered job.
//dummy: a parameter used to prevent triggering the job with the same parameters value.
//this parameter has to accept a different value each time the job is triggered.
//def value=it.value
//def jobname=it.key
    build job:"Auto Deploy Sanity",parameters: [
      string(name: 'BUILD_VERSION', value:params.BUILD_VERSION),
	  string(name: 'BaseImage', value:base_image),
	  string(name:'ToolType',value:tool_type),
	  string(name:'BUILDTYPE',value:params.BUILDTYPE),
	  string(name: 'BaseImagePath', value:base_image_path),
	  string(name: 'TestSuite', value:test_suite_name),
	  string(name: 'TestSuitePath', value:test_suite_path),
	  string(name: 'ATEBasePath', value:ate_scripts_path),
	  [$class: 'NodeParameterValue', name: 'ATEControllerEngine', labels: ["${ate_controller_engine_label}"], nodeEligibility: [$class: 'AllNodeEligibility']],
      [$class: 'NodeParameterValue', name: 'SimulatorEngine', labels: ["${getNodePerLabel("SimulatorEngine")}"], nodeEligibility: [$class: 'AllNodeEligibility']]]
  }
  i++
}
parallel tests
}


@NonCPS
def getNodePerLabel(String label){
    //read all nodes labels with given label
    def labelNodes=Jenkins.getInstance().getLabel(label)
    def nodes=labelNodes.getNodes()
    def nodeName=null
    //iterating nodes to select free one for execution
    for (node in nodes) {
        //exclude Offline nodes
        if (!node.getComputer().isOffline()){
                 //Select nodes which has free executers and which wasn't already selected by any of the current execution tests
                if ((node.getComputer().getNumExecutors()-node.getComputer().countBusy() >0) && !(node.getNodeName() in allocated_nodes)){
                    nodeName=node.getNodeName()
                    //Update global list for that node was selected for execution
                    allocated_nodes << nodeName
                    break;
                }
             }
    }
    //In case there is no nodes with free execution, take last selected node (test will be pending till once of the node executers released)
    if (nodeName == null)
        nodeName=nodes[0].getNodeName();
    return nodeName
}
